query GetDoctorByCaseId(
  $caseId: String!
  $doctorId: String!
)
{
  doctor(filter: $doctorId) {
    edges {
      node {
        id
        cases(filter: $caseId) {
          edges {
            node {
              id
              audio {
                edges {
                  node {
                    id
                    fileId
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
