query GetDoctorByCaseId($caseId: String!)
{
  doctor {
    edges {
      node {
        id
        cases(filter: $caseId) {
          edges {
            node {
              id
              audio {
                edges {
                  node {
                    id
                    fileId
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
