query:
mutation attachTranscription (
  $doctorId: ID!
  $caseId: ID!
  $text: String!
  )
{
  doctor(op: UPSERT, data:{id:$doctorId}) {
    edges {
      node {
        cases(op: UPSERT, data:{id: $caseId, transcribed: true, transcribedText: $text}) {
          edges {
            node {
              transcribed
              transcribedText
            }
          }
        }
      }
    }
  }
}
