mutation CreateDoctorCaseAutioOnce(
  $fileId: String!
  ) {
  doctor(op: UPSERT, data: {name: "Jack", oidcId: "awxvt62dxa", cases: {title: "test01", patient: {
    name: "Peter"
  }, audio: {fileId: $fileId, filename: "hello", fileType: "mpeg"}}}) {
    edges {
      node {
        id
        oidcId
        name
        cases {
          edges {
            node {
              id
              title
              transcribedText
              patient {
                edges {
                  node {
                    id
                    name
                  }
                }
              }
              audio {
                edges {
                  node {
                    fileId
                    filename
                    fileType
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
